INSERT INTO `wp_postmeta` VALUES("278", "258", "Accordion_Settings", "s:570:\"a:18:{s:13:\"acc_sec_title\";s:2:\"no\";s:10:\"op_cl_icon\";s:3:\"yes\";s:14:\"acc_title_icon\";s:3:\"yes\";s:10:\"acc_radius\";s:3:\"yes\";s:10:\"acc_margin\";s:3:\"yes\";s:15:\"acc_op_cl_align\";s:5:\"right\";s:13:\"enable_toggle\";s:2:\"no\";s:16:\"enable_ac_border\";s:3:\"yes\";s:13:\"expand_option\";s:1:\"1\";s:9:\"ac_styles\";s:1:\"1\";s:16:\"acc_title_bg_clr\";s:7:\"#e8e8e8\";s:18:\"acc_title_icon_clr\";s:7:\"#003b56\";s:15:\"acc_desc_bg_clr\";s:7:\"#ffffff\";s:17:\"acc_desc_font_clr\";s:7:\"#000000\";s:10:\"title_size\";s:2:\"21\";s:8:\"des_size\";s:2:\"21\";s:11:\"font_family\";s:9:\"Open Sans\";s:10:\"custom_css\";s:0:\"\";}\";");
