INSERT INTO `wp_posts` VALUES("343", "1", "2018-06-08 21:36:57", "2018-06-08 18:36:57", "<div class=\"wrapper_text\">\n<h1>Страхование грузоперевозок</h1>\n   Страхованию подлежит ущерб, который может произойти во время транспортировки грузов:\n<ul>\n	<li>повреждение или полная гибель груза;</li>\n	<li>ущерб от аварии, которая произошла при попытке спасти застрахованный груз;</li>\n	<li>пропажа средства перевозки без вести (автомобиль, корабль, самолет)</li>\n	<li>ущерб, причиненный грузу в следствии погрузки, разгрузки, заправкой судном топлива;</li>\n	<li>ущерб при общей аварии</li>\n</ul>\n<hr>\n<h2>Программы страхования здоровья</h2>\n<p>В зависимости от видов покрываемых рисков, мы можем предложить следующие программы либо их комбинации:</p>\n<ul>\n	<li><span class=\"akcent\">Програма \"К\"</span> – \"Консультационно-диагностична помощь\"</li>\n	<li><span class=\"akcent\">Програма \"А\"</span> - \"Амбулаторно-поликлиническое лечение\"</li>\n	<li><span class=\"akcent\">Програма \"С\"</span> - \"Стационарное лечение\"</li>\n	<li><span class=\"akcent\">Програма \"Н\"</span>- \"Скорая медицинская помощь\"</li>\n	<li><span class=\"akcent\">Програма \"З\"</span> - \"Стоматологическая помощь\"</li>\n	<li><span class=\"akcent\">Програма \"Д\"</span> - \"Страхование детей\".</li>\n\n</ul>\n\n</div>", "Страхование грузоперевозок", "", "inherit", "closed", "closed", "", "341-autosave-v1", "", "", "2018-06-08 21:36:57", "2018-06-08 18:36:57", "", "341", "http://strahovanie.ru/341-autosave-v1/", "0", "revision", "", "0");
